css:
 body{
            font-family: sans-serif;
        }
        .table-bordered{
            font-size: 8.5pt;
        }
        .border-top {
            border-top: 3px solid #f47142 !important;
        }
        .header_column{
         position:relative;
        }
        .img_logo{
         position:absolute;
         left:0;
        }
        .company-header-info{
            font-size: 12px;
            line-height: 18px;
            left:0 !important;
        }
        .company-header-info h4{
            font-size: 14px;
            margin: 5px 0;
        }
        .print-tag h5 {
            margin: 0 0 5px;	  
        }
.doc_title{font-size:22px;}
.break-table {border-top:0 !important;}
.break-table .break-table-field {padding:0 6px !important;}
.break-table-field table.table-bordered {border-top:0 !important;}
.print-format td.normal-table {padding-bottom:0 !important;}
.print-format hr{
    margin-bottom: 5px;
    margin-top: 5px;
    border: 1px solid #f47142;
}
.table-bordered > thead > tr > th, .table-bordered > tbody > tr > th, .table-bordered > tfoot > tr > th, .table-bordered > thead > tr > td, .table-bordered > tbody > tr > td, .table-bordered > tfoot > tr > td {
   border: 2px solid #d1d8dd;
}

.print-format table {
    margin-bottom: 0px !important;
    margin-top: 0px !important;
}

.table-bordered > thead > tr > th, .table-bordered > tbody > tr > th, .table-bordered > tfoot > tr > th, .table-bordered > thead > tr > td, .table-bordered > tbody > tr > td, .table-bordered > tfoot > tr > td {
    border: 2px solid #d1d8dd;
}
.tax-break-up thead {
   border: 2px solid #d1d8dd;
}
.tax-break-up th {
   text-align:center;
}

.tax-break-up > table{
width:100%;
}
